const byteCodeNft = ''

async function deploy() {
    const publicAddress = await wallet.getAddress();
    console.log(`钱包地址${publicAddress}`)

    const factoryNft = new ethers.ContractFactory(nftAbi, byteCodeNft, wallet);
    // console.log("等待NFT合约部署上链")
    const Vinci = 'vinci';
    const VIC = 'VIC';
    const contractNft = await factoryNft.deploy(Vinci, VIC, {
        gasLimit: 30000000
    });

    console.log("NFT合约地址")
    console.log(contractNft.address);
    console.log("等待NFT合约部署上链");
    await contractNft.deployed()
    console.log("NFT合约上链成功")
    return contractNft.address;

}
